mapper.select:
select rPM.CName,rPM.PMCode,t.* from(select brTr.PhaseName,brTr.PMDisc,wo.WorkOrderID,dense_rank() OVER (partition by SUBSTRING(his.Recipe,charindex('ADD_',his.Recipe) ,len(his.Recipe)) ORDER BY his.RecordNo) as AddIndex,his.* from [BatchHistoryEx].[dbo].[BHBatchHis] his,WorkOrder wo,BatchRecordTr brTr where his.BatchID=wo.FormulaId collate chinese_prc_ci_as and his.Descript=brTr.PMName collate chinese_prc_ci_as and his.Phase=brTr.PhaseName collate chinese_prc_ci_as
#{workOrderID}
and his.Descript='ADDED_AMOUNT' and his.Event='report') t,RecipePM rPM where 1=1 and t.PMDisc=case when charindex('_',rPM.CName)>0 then substring(rPM.CName,0,charindex('_',rPM.CName)) else rPM.CName end collate chinese_prc_ci_as and t.AddIndex=substring(rPM.PMName,len(rPM.PMName),1) collate chinese_prc_ci_as and t.WorkOrderID=rPM.WorkOrderID

select wo.WorkOrderID,his.* from [BatchHistoryEx].[dbo].[BHBatchHis] his,WorkOrder wo,BatchRecordTr brTr where his.BatchID=wo.FormulaId collate chinese_prc_ci_as and his.Phase=brTr.PhaseName collate chinese_prc_ci_as
#{workOrderID}
and his.Descript='State Changed: ' and his.PValue='COMPLETE'

select brTr.PhaseDisc,wo.WorkOrderID,his.BatchID,his.Recipe,Phase,brTr.PMDisc,his.Descript,his.PValue,his.LclTime from [BatchHistoryEx].[dbo].[BHBatchHis] his,WorkOrder wo,BatchRecordTr brTr where his.BatchID=wo.FormulaId collate chinese_prc_ci_as and his.Descript=brTr.PMName collate chinese_prc_ci_as and his.Phase=brTr.PhaseName collate chinese_prc_ci_as and EU!=''
#{workOrderID}
and his.Event='Recipe Value' and (his.Descript='TIME' or his.Descript='VD') group by brTr.PhaseDisc,wo.WorkOrderID,his.BatchID,his.Recipe,his.Phase,brTr.PMDisc,his.Descript,his.PValue,his.LclTime

select brTr.PhaseDisc,wo.WorkOrderID,his.BatchID,his.Recipe,Phase from [BatchHistoryEx].[dbo].[BHBatchHis] his,WorkOrder wo,BatchRecordTr brTr where his.BatchID=wo.FormulaId collate chinese_prc_ci_as and his.Phase=brTr.PhaseName collate chinese_prc_ci_as and EU!=''
#{workOrderID}
group by brTr.PhaseDisc,wo.WorkOrderID,his.BatchID,his.Recipe,his.Phase
