mapper.select:
select brTr.PMDisc,brTr.PhaseName,wo.WorkOrderID,rPM.PMCode,his.* from [BatchHistoryEx].[dbo].[BHBatchHis] his,WorkOrder wo,BatchRecordTr brTr,RecipePM rPM where his.BatchID=wo.FormulaId collate chinese_prc_ci_as and his.Descript=brTr.PMName collate chinese_prc_ci_as and his.Phase=brTr.PhaseName collate chinese_prc_ci_as and brTr.PMDisc=rPM.CName collate chinese_prc_ci_as
#{workOrderID}
and his.Descript='ADDED_AMOUNT' and his.Event='report'

select brTr.PhaseDisc,wo.WorkOrderID,his.BatchID,his.Recipe,Phase,EU from [BatchHistoryEx].[dbo].[BHBatchHis] his,WorkOrder wo,BatchRecordTr brTr where his.BatchID=wo.FormulaId collate chinese_prc_ci_as and his.Phase=brTr.PhaseName collate chinese_prc_ci_as and EU!=''
#{workOrderID}
group by brTr.PhaseDisc,wo.WorkOrderID,his.BatchID,his.Recipe,his.Phase,his.EU
